CREATE TABLE albums (aid BIGINT UNSIGNED AUTO_INCREMENT, created DATETIME DEFAULT '0000-00-00 00:00:00' NOT NULL, mod DATETIME DEFAULT '0000-00-00 00:00:00' NOT NULL, cover_pid BIGINT NOT NULL, name TEXT NOT NULL, description TEXT NOT NULL, sort_order BIGINT NOT NULL, PRIMARY KEY(aid)) ENGINE = INNODB;
CREATE TABLE comments (cid BIGINT UNSIGNED AUTO_INCREMENT, pid BIGINT NOT NULL, created DATETIME NOT NULL, subject_name VARCHAR(50) NOT NULL, subject_id VARCHAR(20) NOT NULL, text TEXT NOT NULL, PRIMARY KEY(cid)) ENGINE = INNODB;
CREATE TABLE photos (pid BIGINT UNSIGNED AUTO_INCREMENT, created DATETIME DEFAULT '0000-00-00 00:00:00' NOT NULL, mod DATETIME DEFAULT '0000-00-00 00:00:00' NOT NULL, caption TEXT NOT NULL, file TEXT NOT NULL, aid BIGINT NOT NULL, sort_order BIGINT NOT NULL, PRIMARY KEY(pid)) ENGINE = INNODB;
CREATE TABLE tags (tid BIGINT UNSIGNED AUTO_INCREMENT, pid BIGINT NOT NULL, created DATETIME DEFAULT '0000-00-00 00:00:00' NOT NULL, subject_id VARCHAR(10) NOT NULL, subject_name TEXT NOT NULL, xcoord VARCHAR(8) NOT NULL, ycoord VARCHAR(8) NOT NULL, PRIMARY KEY(tid)) ENGINE = INNODB;
